import React from 'react';
import { 
  Zap, 
  CheckCircle, 
  Clock, 
  AlertCircle,
  TrendingUp,
  Music,
  Share2,
  FileText,
  ArrowRight
} from 'lucide-react';
import StatCard from '../components/StatCard';
import QuickActionCard from '../components/QuickActionCard';
import RecentActivity from '../components/RecentActivity';

export default function Home() {
  const stats = [
    { 
      label: 'Active Projects', 
      value: '3', 
      icon: Zap, 
      color: 'from-raven-orange to-orange-400',
      trend: '+1 this week'
    },
    { 
      label: 'Tasks To Do', 
      value: '8', 
      icon: Clock, 
      color: 'from-yellow-500 to-yellow-400',
      trend: '3 due today'
    },
    { 
      label: 'In Progress', 
      value: '4', 
      icon: TrendingUp, 
      color: 'from-blue-500 to-blue-400',
      trend: 'On track'
    },
    { 
      label: 'Completed', 
      value: '12', 
      icon: CheckCircle, 
      color: 'from-raven-teal to-teal-400',
      trend: 'This month'
    },
  ];

  const quickActions = [
    { 
      label: 'Morning Briefing', 
      icon: Zap, 
      desc: 'View today\'s briefing',
      color: 'from-raven-orange/20 to-raven-orange/5',
      onClick: () => console.log('briefing')
    },
    { 
      label: 'Spotify Stats', 
      icon: Music, 
      desc: 'Check listening data',
      color: 'from-green-500/20 to-green-500/5',
      onClick: () => console.log('spotify')
    },
    { 
      label: 'Social Overview', 
      icon: Share2, 
      desc: 'YouTube, Twitter status',
      color: 'from-blue-500/20 to-blue-500/5',
      onClick: () => console.log('social')
    },
    { 
      label: 'New Task', 
      icon: FileText, 
      desc: 'Add to Kanban board',
      color: 'from-purple-500/20 to-purple-500/5',
      onClick: () => console.log('task')
    },
  ];

  const activities = [
    { 
      type: 'task', 
      title: 'Created Joveheal Project Tracker', 
      time: '2 hours ago',
      status: 'success'
    },
    { 
      type: 'social', 
      title: 'YouTube channel connected (@raven27n)', 
      time: '4 hours ago',
      status: 'success'
    },
    { 
      type: 'file', 
      title: 'Updated PROJECT.md spec', 
      time: '5 hours ago',
      status: 'success'
    },
    { 
      type: 'notion', 
      title: 'Added 3 tasks to Joveheal database', 
      time: '6 hours ago',
      status: 'success'
    },
  ];

  return (
    <div className="space-y-6">
      {/* Welcome */}
      <div className="mb-8">
        <h2 className="text-3xl font-bold mb-2">
          Good afternoon, <span className="gradient-text">Sammy</span>
        </h2>
        <p className="text-raven-muted">Here's what's happening with your projects today.</p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {stats.map((stat) => (
          <StatCard key={stat.label} {...stat} />
        ))}
      </div>

      {/* Two Column Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Quick Actions */}
        <div className="lg:col-span-2 space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold">Quick Actions</h3>
            <button className="text-sm text-raven-orange hover:text-raven-teal transition-colors flex items-center gap-1">
              View All <ArrowRight size={14} />
            </button>
          </div>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            {quickActions.map((action) => (
              <QuickActionCard key={action.label} {...action} />
            ))}
          </div>

          {/* System Status */}
          <div className="mt-6 bg-raven-card rounded-xl p-6 border border-raven-border">
            <h3 className="text-lg font-semibold mb-4">System Status</h3>
            <div className="space-y-3">
              {[
                { name: 'Notion API', status: 'Connected', icon: '✓', color: 'text-green-400' },
                { name: 'YouTube API', status: 'Connected', icon: '✓', color: 'text-green-400' },
                { name: 'Spotify API', status: 'Pending Setup', icon: '○', color: 'text-yellow-400' },
                { name: 'Facebook Page', status: 'Not Created', icon: '✕', color: 'text-red-400' },
              ].map((service) => (
                <div key={service.name} className="flex items-center justify-between py-2 border-b border-raven-border last:border-0">
                  <span className="text-raven-text">{service.name}</span>
                  <span className={`flex items-center gap-2 ${service.color}`}>
                    <span>{service.icon}</span>
                    <span className="text-sm">{service.status}</span>
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="space-y-4">
          <h3 className="text-lg font-semibold">Recent Activity</h3>
          <RecentActivity activities={activities} />
        </div>
      </div>
    </div>
  );
}
